package day1;

public class Day1 {


    private final static String INPUT = "";


    private final static String TEST_INPUT1 = "1122";
    private final static String TEST_INPUT2 = "1111";
    private final static String TEST_INPUT3 = "1234";
    private final static String TEST_INPUT4 = "91212129";


    private final static String PT2_TEST_INPUT1 = "1212";
    private final static String PT2_TEST_INPUT2 = "1221";
    private final static String PT2_TEST_INPUT3 = "123425";
    private final static String PT2_TEST_INPUT4 = "123123";
    private final static String PT2_TEST_INPUT5 = "12131415";


    public static void main(String[] args) {
        //PART 1
        System.out.println("P1 INPUT1: " + inverseCaptcha(TEST_INPUT1, 1));
        System.out.println("P1 INPUT2: " + inverseCaptcha(TEST_INPUT2, 1));
        System.out.println("P1 INPUT3: " + inverseCaptcha(TEST_INPUT3, 1));
        System.out.println("P1 INPUT4: " + inverseCaptcha(TEST_INPUT4, 1));
        System.out.println("P1 INPUT: " + inverseCaptcha(INPUT, 1) + "\n");

        //PART 2
        System.out.println("P2 INPUT1: " + inverseCaptcha(PT2_TEST_INPUT1, (PT2_TEST_INPUT1.length() / 2)));
        System.out.println("P2 INPUT2: " + inverseCaptcha(PT2_TEST_INPUT2, (PT2_TEST_INPUT2.length() / 2)));
        System.out.println("P2 INPUT3: " + inverseCaptcha(PT2_TEST_INPUT3, (PT2_TEST_INPUT3.length() / 2)));
        System.out.println("P2 INPUT4: " + inverseCaptcha(PT2_TEST_INPUT4, (PT2_TEST_INPUT4.length() / 2)));
        System.out.println("P2 INPUT4: " + inverseCaptcha(PT2_TEST_INPUT5, (PT2_TEST_INPUT5.length() / 2)));
        System.out.println("P2 INPUT: " + inverseCaptcha(INPUT, (INPUT.length() / 2)));
    }


    public static int inverseCaptcha(String s, int offset) {
        int sum = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            int currentNum = Character.getNumericValue(s.charAt(i));
            int indexOfNext = (i + offset) % s.length();
            int nextNum = Character.getNumericValue(s.charAt(indexOfNext));

            if (currentNum == nextNum) {
                sum += currentNum;
            }
        }

        return sum;
    }

}
